import styled from "styled-components";
import React from 'react'
import Navbar from '../components/Navbar'
import Announcement from '../components/Announcement'
import Newsletter from '../components/Newsletter'
import Footer from '../components/Footer';

const Container = styled.div``
const Wrapper = styled.div`
padding:50px;
display:flex;

`;
const ImgContainer = styled.div`
flex:1;
`
const Image = styled.img`
width:100%;
height:90vh;
object-fit:cover;
`
const InfoContainer = styled.div`
flex:1
padding:0px 50px;

`
const Title = styled.h1`


`
const Desc = styled.p`
margin: 20px 0px;
`
const Price = styled.span`
 font-width:100;
 font-size:40px;
`
const FilterContainer = styled.div`
width:50%;
margin:30px 0px;
display:flex;
justify-content:Space-between;
`
const Filter = styled.div`
display:flex;
align-items:center;
`
const FilterTitle = styled.span`
font-size:20px;
font-weight:200;
border-radius:50%;
background-color:${props=>props.color}
margin:0px 5px;
cursor: pointer;

`
const FilterColor = styled.div``
const FilterSize= styled.select``
const FilterSizeOption = styled.option``




const Product = () => {
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
          <ImgContainer>
          <Image src="http://thoitrangnuuyenuyen.com/watermark/product/540x540x1/upload/product/073-1-2531.jpg"/>
          </ImgContainer>
          <InfoContainer >
         <Title>Đầm Xếp Ly Eo Phối Cổ Trắng</Title>
         <Desc>Đầm được thiết kế nữ tính, sang trọng cho sp, tỉ mỉ trong từng đường may, tạo cảm giác tự tin hơn khi mặc.</Desc>
         <Price>290.000 đ</Price>
         <FilterContainer>
             <Filter>
                 <FilterTitle>Color</FilterTitle>
                 <FilterColor color="Black"/>
                 <FilterColor color="Gray"/>
                 <FilterColor color="Blue"/>
                 <FilterColor color="Darkblue"/>
                 <FilterColor color="Yellow"/>
             </Filter>
             <FilterTitle>Size</FilterTitle>
             <FilterSize>
                 <FilterSizeOption>XS</FilterSizeOption>
                 <FilterSizeOption>S</FilterSizeOption>
                 <FilterSizeOption>M</FilterSizeOption>
                 <FilterSizeOption>L</FilterSizeOption>

             </FilterSize>
         </FilterContainer>
          </InfoContainer>
      </Wrapper>
      <Newsletter/>
      <Footer/>
    </Container>
  )
}

export default Product
